document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("newsletterForm"),t=document.getElementById("newsletterEmail"),s=e.querySelector(".form-message");e.addEventListener("submit",async r=>{r.preventDefault(),s.textContent="",s.style.color="";const n=t.value.trim();if(!function(e){return/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(e)}(n))return s.textContent="Please enter a valid email address.",s.classList.add("error","show"),void t.focus();try{await new Promise(e=>{setTimeout(()=>{e()},1e3)}),s.textContent="Thank you for subscribing!",s.classList.add("success","show"),e.reset(),setTimeout(()=>{s.classList.remove("show")},5e3)}catch(e){s.textContent="An error occurred. Please try again later.",s.classList.add("error","show")}})});