document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("newsletterForm"),t=document.getElementById("newsletterEmail"),n=e.querySelector(".form-message");e.addEventListener("submit",async r=>{r.preventDefault(),n.textContent="",n.style.color="";const o=t.value.trim();if(!function(e){return/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(e)}(o))return n.textContent="Please enter a valid email address.",n.style.color="red",void t.focus();try{await new Promise(e=>{setTimeout(()=>{e()},1e3)}),n.textContent="Thank you for subscribing!",n.style.color="green",e.reset()}catch(e){n.textContent="An error occurred. Please try again later.",n.style.color="red"}})});